print(f'  ETH balance: {eth_balance}')
price_1_steth_to_eth = Decimal(curve_fi_steth_swap_contract.get_dy(1, 0, 10**18))/Decimal(10**18)
print(f'    stETH/ETH: {price_1_steth_to_eth}')

input_steth_right = int(eth_balance)
input_steth_left = 0
input_steth_middle = int((input_steth_left + input_steth_right)/2)


@lru_cache(1000)
def get_price_near_input_steth(_input_steth: int):
    return (Decimal(curve_fi_steth_swap_contract.get_dy(1, 0, (_input_steth+1)*10**18)) - Decimal(curve_fi_steth_swap_contract.get_dy(1, 0, _input_steth*10**18)))/Decimal(10**18)


wanted_price = 0.85

for i in range(10):
    if abs(get_price_near_input_steth(input_steth_middle) - Decimal(wanted_price)) < 0.0005:
        break
    if abs(input_steth_right - input_steth_left) <= 1:
        break
    if get_price_near_input_steth(input_steth_middle) < wanted_price:
        input_steth_right = input_steth_middle
    else:
        input_steth_left = input_steth_middle
    input_steth_middle = int((input_steth_left + input_steth_right) / 2)
print(f'        delta: {input_steth_middle} stETH; stETH/ETH={get_price_near_input_steth(input_steth_middle)} within {i} attempts to solve')

