@lru_cache(1000)
def get_price_near_input_steth(_input_steth: int):
    return (Decimal(get_dy(1, 0, (_input_steth+1)*10**18)) - Decimal(get_dy(1, 0, _input_steth*10**18)))/Decimal(10**18)


